Console.WriteLine("Введите X первого числа:");double x1 = double.Parse(Console.ReadLine());Console.WriteLine("Введите Y первого числа:");double y1 = double.Parse(Console.ReadLine());Console.WriteLine("Введите Z первого числа:");double z1 = double.Parse(Console.ReadLine());Console.WriteLine("Введите X второго числа:");double x2 = double.Parse(Console.ReadLine());Console.WriteLine("Введите Y второго числа:");double y2 = double.Parse(Console.ReadLine());Console.WriteLine("Введите Z второго числа:");double z2 = double.Parse(Console.ReadLine());double ab = Math.Pow(x2 - x1, 2) + Math.Pow(y2 - y1, 2) + Math.Pow(z2 - z1, 2);double AB = Math.Round(Math.Sqrt(ab), 3);Console.WriteLine(AB);
